import {useState, useEffect} from 'react';
import { useLocation } from "react-router-dom";

import axios from 'axios'


function Profile(){

    //let history = useHistory();
    
    const location = useLocation();
    const recieved_mail = location.state.params;
    useEffect(() => {
        console.log("Use Effect Processed!");
    });

    const [name,setName]=useState("");
    const [email,setEmail]=useState(recieved_mail);
    const [mobile,setMobile]=useState("");
    const [password,setPassword]=useState("");
    

    axios.post("/reqData",{
        
        email: recieved_mail,
    }).then((res) => {
        console.log(res.data)
        setName(res.data[0].name)
        setMobile(res.data[0].contact)
        setPassword(res.data[0].password)
       
      
    });

    
    

    return(

            
        <div>
            <br></br>

            <center>
            <form >
                <label  >Name: </label>
                <input id = "name" type = "text" placeholder="Enter Name" value={name} onChange={(e)=> setName(e.target.value)}></input><br/><br/>
                <label >Email: </label>
                <input id="email"type = "text" placeholder="Enter Email" value={email} onChange={(e)=> setEmail(e.target.value)} readOnly></input><br/><br/>
                <label >Contact: </label>
                <input id="contact"type = "text" placeholder="Enter Number" value={mobile} onChange={(e)=> setMobile(e.target.value)}></input><br/><br/>
                <label >Password: </label>
                <input id="passsword" type = "password" placeholder="Enter Password" value={password} onChange={(e)=> setPassword(e.target.value)}></input><br/><br/>
                <button type="submit">Update</button>
            </form>    
                
            </center>

            
            

        </div>
    
    )
}

export default Profile
