import {useState, useEffect} from 'react';
import { useLocation } from "react-router-dom";
import { useHistory } from "react-router-dom";

import axios from 'axios'


function Profile(){

    let history = useHistory();
    
    const location = useLocation();
    const recieved_mail = location.state.params;
    useEffect(() => {
        console.log("Use Effect Processed!");
    });

   
    
    const sendPostRequest = async () => {
        try {

            await axios.post("/reqData",{
                
                email: recieved_mail,
            }).then((res) => {
                console.log(res.data)
              
            
                return(JSON.parse(JSON.stringify(res)));
            });

        
        } catch (err) {
        // Handle Error Here
        console.error(err);
        }
    }

    

    const [name,setName]=useState(sendPostRequest().data[0].name);
    const [email,setEmail]=useState(recieved_mail);
    const [mobile,setMobile]=useState(sendPostRequest().data[0].contact);
    const [password,setPassword]=useState(sendPostRequest().data[0].password);
    function submit(e){

        e.preventDefault();
        axios.post("/updateData",{
            name:name,
            email:email,
            password: password,
            contact:mobile
        }).then((res) => {
            console.log(res.data)
            history.push('/profile',{params:email})
          
        });
    }

    
    

    return(

            
        <div>
            <br></br>

            <center>
            <h1>Profile</h1>
            <form onSubmit={(e) => submit(e)}>
                <label  >Name: </label>
                <input id = "name" type = "text" placeholder="Enter Name" value={name} onChange={(e)=> setName(e.target.value)}></input><br/><br/>
                <label >Email: </label>
                <input id="email"type = "text" placeholder="Enter Email" value={email} onChange={(e)=> setEmail(e.target.value)} readOnly></input><br/><br/>
                <label >Contact: </label>
                <input id="contact"type = "text" placeholder="Enter Number" value={mobile} onChange={(e)=> setMobile(e.target.value)}></input><br/><br/>
                <label >Password: </label>
                <input id="passsword" type = "password" placeholder="Enter Password" value={password} onChange={(e)=> setPassword(e.target.value)}></input><br/><br/>
                <button type="submit">Update</button>
            </form>    
                
            </center>

            
            

        </div>
    
    )
}

export default Profile
